import { React, useState, useEffect } from "react";
import { motion } from "framer-motion";
import "./Hero.css";
const Hero = () => {
  const renderImage = (e) => {
    const Images = [
      { image: "/assets/DSC_0008.JPG" },
      { image: "/assets/DSC_0009.JPG" },
      { image: "/assets/DSC_0019.JPG" },
      { image: "/images/DSC_0073.JPG" },
      { image: "/images/DSC_0074.JPG" },
      { image: "/images/DSC_0088.JPG" },
      { image: "/images/DSC_0144.JPG" },
    ];
    const randomImageIndex = Math.floor(Math.random() * Math.floor(7));
 console.log(Images);
    return Images[randomImageIndex].image;
  };
  const [randomImage, setRandomImage] = useState(renderImage());
  // const [randomImage1, setRandomImage1] = useState(renderImage());
  useEffect(() => {
    setRandomImage(renderImage());
    // setRandomImage1(renderImage());
  }, []);
  return (
    <section className="hero">
      <motion.div
        className="hero-content"
        style={{ backgroundImage: `url(${randomImage})` }}
        animate={{ rotate: [0, 360] }}
        transition={{ duration: 3, repeat: Infinity }}
      >
        {/* <h1></h1> */}
        {/* <h2> </h2> */}
        {/* <button className="cta-button">Get Started</button> */}
      </motion.div>
    </section>
  );
};

export default Hero;
