import { React, useState, useEffect } from "react";
import { motion } from "framer-motion";
import "./Hero.css";
import { image1 } from "../heroImages/DSC_0002.jpg"
import { image2 } from "./heroImages/DSC_0008.jpg"

const Images = [

  // { image:  "../../../public/images/DSC_0008.jpg" },
  // { image:  "../../../public/images/DSC_0009.jpg" },
  // { image:  "../../../public/images/DSC_0019.jpg"},
  { image: image1},
  { image: image2 },
  // { image: "url(/images/DSC_0088.jpg)" },
  // { image: "url(images/DSC_0144.jpg)" },
];



const Hero = () => {
  const [imageIndex, setImageIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setImageIndex((prevIndex) => (prevIndex + 1) % Images.length);
    }, 3000);
    return () => clearInterval(intervalId);
  }, []);

  return (
    <section className="hero">
      <motion.div
        className="hero-content"
        style={{ backgroundImage: `url(${Images[imageIndex].image})` }}
        animate={{ y: ["-5px", "5px", "-5px"] }}
        transition={{ duration: 3, repeat: Infinity }}
      >
        {/* <h1></h1> */}
        {/* <h2> </h2> */}
        {/* <button className="cta-button">Get Started</button> */}
      </motion.div>
    </section>
  );
};

export default Hero;
